import { useState } from "react";


// Initial packing items
const initialItems = [
  { id: 1, description: "Shirt", quantity: 5, packed: false },
  { id: 2, description: "Pants", quantity: 2, packed: true },
];

function Logo() {
  return <h1>My Travel List</h1>;
}

function Form({ handelAddItem }) {
  const [description, setDescription] = useState("")
  const [quantity, setQuantity] = useState(1)
  const [fav,setFav]=useState(false)


  function handelSubmit(e) {
    e.preventDefault()
    const item = {
      id: Date.now(),
      description,
      quantity,
      packed: false,
      fav
    };
    handelAddItem(item)
    setDescription("");
    setQuantity(1);
    setFav(false);
  }

  function toggleFav() {
    setFav((prev) => !prev); // Toggle the favorite state
  }

  return (
    <form className="add-form" onSubmit={handelSubmit}>
      <h3>What do you need to pack? </h3>
      <select id="quantity" value={quantity} onChange={(e) => setQuantity(Number(e.target.value))}>
        <option value={1}>1</option>
        <option value={2} >2</option>
        <option value={3}>3</option>
      </select>
      <input type="text" placeholder="Item..." value={description} onChange={(e) => setDescription(e.target.value)}></input>
      <i className={!fav?"fa-regular fa-star": "fa-solid fa-star"} onClick={toggleFav} ></i>
      <button type="submit">Add</button>
    </form>
  );
}

function Item({ item }) {
  let itemStyle;

  if (item.packed===true) {
    itemStyle = { textDecoration: "line-through"}; 
  } else if (item.fav===true) {
    itemStyle = { background: "#f4a226" }; 
  } else {
    itemStyle = { textDecoration: "none", color: "black" }; 
  }
  return (
    <li style={itemStyle}>
      {item.description} ({item.quantity})
    </li>
  )
}
function Search({ setSearchQuery }) {
  const [search, setSearch] = useState("")

  function handelSearch(e) {
    e.preventDefault();
    setSearchQuery(search);
  }
  function handleShowAll() {
    setSearch("");
    setSearchQuery(""); // Reset searchQuery to show all items
  }


  return (
    <form className="search-form" onSubmit={handelSearch}>
      <input type="text" placeholder="Search item..." value={search} onChange={(e) => setSearch(e.target.value)}></input>
      <button type="submit">Search</button>
      <button type="button" onClick={handleShowAll}>
        All
      </button>
    </form>
  )
}
function PackingList({items}) {
  const [searchQuery, setSearchQuery] = useState("");

  const filteredItems = items.filter((item) =>
    item.description.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="bg-color">
       <Search setSearchQuery={setSearchQuery} />
      <div className="list">
        <ul>
        {filteredItems.map((item) => (
          <Item key={item.id} item={item} />
        ))}
        </ul>
      </div>
    </div>
  );
}

function Stats() {
  return (
    <footer className="stats">
      <em>You have X items in the list. You already packed Y (Z%).</em>
    </footer>
  );
}

function App() {
  const [items, setItems] = useState([]) 
  function handelAddItem(item) {
    setItems((prevItems) => [...prevItems, item]);
  }
  return (
    <div className="app">
      <Logo />
      <Form handelAddItem={handelAddItem} />
      <PackingList items={items}  />
      <Stats />
    </div>
  );
}

export default App;
