import { useState } from "react";

// Initial packing items
const initialItems = [
  { id: 1, description: "Shirt", quantity: 5, packed: false },
  { id: 2, description: "Pants", quantity: 2, packed: true },
];

function Logo() {
  return <h1>My Travel List</h1>;
}

function Form() {
  const[description,setDescription]=useState("")
  const[quantity,setQuantity]=useState(1)
  const[item,setItem]=useState([])

  function handelAddItem(item){
    setItem((prevItems) => [...prevItems, item]);
  }

  function handelSubmit(){
    e.preventDefault()
    const item={
      id: Date.now(),
      description,
      quantity,
      packed: false,
    };
    handelAddItem(item)
    setDescription()
  }

  return (
    <form className="add-form" onSubmit={handelSubmit}>
      <h3>What do you need to pack?</h3>
      <select id="quantity" value={quantity} onChange={(e)=> setQuantity(Number(e.target.value))}>
        <option value={1}>1</option>
        <option value={2} >2</option>
        <option value={3}>3</option>
      </select>
      <input type="text" placeholder="Item..." value={description} onChange={(e)=> setDescription(e.target.value)}></input>
      <button type="submit">Add</button>
    </form>
  );
}

function Item({item}){
  return(
    <li style={{textDecoration:item.packed ? "line-through":""}}>
      {item.description} ({item.quantity})
    </li>
  )
}

function PackingList() {
  return (
    <div className="list">
      <ul>
        {initialItems.map((item) => (
          <Item key={item.id} item={item}/>
        ))}
      </ul>
    </div>
  );
}

function Stats() {
  return (
    <footer className="stats">
      <em>You have X items in the list. You already packed Y (Z%).</em>
    </footer>
  );
}

function App() {
  return (
    <div className="app">
      <Logo />
      <Form />
      <PackingList />
      <Stats />
    </div>
  );
}

export default App;
