import { useState } from "react";

function Logo() {
  return <h1>My Travel List</h1>;
}

function Form({ handelAddItem }) {
  const [description, setDescription] = useState("")
  const [quantity, setQuantity] = useState(1)
  const [necessary,setNecessary]=useState(false)


  function handelSubmit(e) {
    e.preventDefault()
    const item = {
      id: Date.now(),
      description,
      quantity,
      packed: false,
      necessary
    };
    handelAddItem(item)
    setDescription("");
    setQuantity(1);
    setNecessary(false);
  }

  function toggleFav() {
    setNecessary((prev) => !prev); 
  }

  return (
    <form className="add-form" onSubmit={handelSubmit}>
      <h3>What do you need to pack? </h3>
      <select id="quantity" value={quantity} onChange={(e) => setQuantity(Number(e.target.value))}>
        <option value={1}>1</option>
        <option value={2} >2</option>
        <option value={3}>3</option>
      </select>
      <input type="text" placeholder="Item..." value={description} onChange={(e) => setDescription(e.target.value)}></input>
      <i className={!necessary?"fa-solid fa-exclamation": "fa-solid fa-exclamation mark"} onClick={toggleFav} ></i>
      <button type="submit">Add</button>
    </form>
  );
}

function Item({ item, handleDelete,handleUpdate }) {
  const itemStyle = {
    textDecoration: item.packed ? "line-through" : "",
    background: item.necessary ? "#f4a226" : "",
    padding: item.necessary ? "1rem" : "",
    borderRadius: item.necessary ? "1rem" : "",
    color: item.necessary ? " #5a3e2b" : "",
  };

  return (
    <li>
      <div style={itemStyle} onClick={handleUpdate}>
      <i className={!item.packed?"fa-regular fa-square":"fa-solid fa-square-check"}></i> 
      {item.description} ({item.quantity}) 
      <i class="fa-solid fa-x" onClick={handleDelete}></i>
      </div>
    </li>
  )
}
function Clear({ setItems }) {
  function handleClearAll() {
    setItems([]);
  }

  function handleClearPacked() {
    setItems((prevItems) => prevItems.filter((item) => !item.packed));
  }

  return (
    <div className="search-form">
      <button className="clear-btns" onClick={handleClearAll}>Clear All</button>
      <button className="clear-btns" onClick={handleClearPacked}>Clear Packed</button>
    </div>
  );
}

function Search({ setSearchQuery }) {
  const [search, setSearch] = useState("")

  function handelSearch(e) {
    e.preventDefault();
    setSearchQuery(search);
  }
  function handleShowAll() {
    setSearch("");
    setSearchQuery(""); 
  }


  return (
    <form className="search-form" onSubmit={handelSearch}>
      <input type="text" placeholder="Search item..." value={search} onChange={(e) => setSearch(e.target.value)}></input>
      <button type="submit">Search</button>
      <button type="button" onClick={handleShowAll}>
        All
      </button>
    </form>
  )
}

function PackingList({items,handleDelete,handleUpdate,setItems}) {
  const [searchQuery, setSearchQuery] = useState("");

  const filteredItems = items.filter((item) =>
    item.description.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="bg-color">
      <div className="top-container">
        <Clear setItems={setItems} />
        <Search setSearchQuery={setSearchQuery} />
      </div>
      <div className="list">
        <ul>
        {filteredItems.map((item) => (
          <Item key={item.id} item={item} handleDelete={() => handleDelete(item.id)} handleUpdate={() => handleUpdate(item.id)} />
        ))}
        </ul>
      </div>
    </div>
  );
}

function Stats({items}) {
  const totalItems=items.length
  const packedItems=items.filter((item)=>item.packed===true).length
  const packedPercentage= Math.round((packedItems/totalItems)*100)
  return (
    <footer className="stats">
      {packedPercentage===100?<em>You got everything!</em>:<em>You have {totalItems} items in the list. You already packed {packedItems} ({packedItems>0?packedPercentage:0}%).</em>}
    </footer>
  );
}

function App() {
  const [items, setItems] = useState([]) 
  function handelAddItem(newItem) {
    setItems((prevItems) => {
      let itemExists = false;


      const updatedItems = prevItems.map((item) => {
        if (item.description.toLowerCase() === newItem.description.toLowerCase()) {
          itemExists = true;
          return { ...item, quantity: item.quantity + newItem.quantity };
        }
        return item;
      });


      if (!itemExists) {
        if (newItem.necessary) {
          return [newItem, ...updatedItems]; 
        } else {
          return [...updatedItems, newItem]; 
        }
      }

      return updatedItems; 
    });
  }

  function handleDelete(id){
    setItems((prevItems)=> prevItems.filter((item)=>item.id!==id));
  }
  function handleUpdate(id){
    setItems((prevItems) =>
    prevItems.map((item) =>
      item.id === id ? { ...item, packed: !item.packed } : item
    )
  );
  }
  return (
    <div className="app">
      <Logo />
      <Form handelAddItem={handelAddItem} />
      <PackingList items={items} handleDelete={handleDelete} handleUpdate={handleUpdate} setItems={setItems} />
      <Stats  items={items} />
    </div>
  );
}

export default App;
